/**
 * 本文件对以下 2 类规则进行降级：
 * - 非 bug 类问题（如风格相关规则）
 * - 因某些业务等原因，无法避免的问题
 */

module.exports = {
  rules: {
    // 统一在点号之前换行
    // @unessential
    'dot-location': ['warn', 'property'],

    // 禁止使用 eval
    // @unessential 有些业务必须使用 eval，不做强制卡点
    // 'no-eval': 'warn',

    // 禁止使用类 eval 的方法，如 setTimeout 传入字符串
    // @unessential 同 eval，有些业务必须使用，不做强制卡点
    // 'no-implied-eval': 'warn',

    // 禁止使用 javascript:url，如 location.href = 'javascript:void(0)';
    // @unessential
    'no-script-url': 'warn',

    // 禁止变量与外层作用域已存在的变量同名
    // @unessential 不做强制卡点
    'no-shadow': 'warn',

    // 禁止出现多个连续空格
    // @unessential
    'no-multi-spaces': [
      'warn',
      {
        ignoreEOLComments: false
      }
    ],

    // 使用 2 个空格缩进
    // @unessential 风格问题，争议很大
    indent: 'off',

    // 使用分号
    // @unessential 风格问题，争议很大
    semi: 'off',

    // 分号必须写在行尾
    // @unessential 配合 semi 关闭
    'semi-style': 'off',
  }
};
